package Week1.day1;

public class NegativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int negativeNumber ;
		negativeNumber = -40;
		int positiveNumber;
		if(negativeNumber < 0) {
			positiveNumber = negativeNumber * (-1);
			System.out.println("Negative number is converted as  positive number " +positiveNumber);
		}
		

	}

}
